package com.ionicframework.IdentityVault;

import android.content.Context;
import android.content.SharedPreferences;
import android.os.Build;
import androidx.annotation.RequiresApi;
import androidx.appcompat.app.AppCompatActivity;

import org.json.JSONException;

import java.nio.charset.StandardCharsets;
import java.security.SecureRandom;
import javax.crypto.Cipher;

@RequiresApi(api = Build.VERSION_CODES.M)
public class DeviceSecurityWeakVault extends VaultBase {
    private boolean authed = false;

    public DeviceSecurityWeakVault(IdentityVaultConfig config, AppCompatActivity activity) {
        super(config, activity);
    }

    @Override
    protected String getVaultType() {
        return VaultType.DEVICE_SECURITY;
    }

    @Override
    public void clear() throws VaultError {
        super.clear();
        this.authed = false;
    }

    @Override
    public void lock() {
        if (!this.isLocked()) {
            this.authed = false;
            super.lock();
        }
    }

    @Override
    public void unlock(boolean forceUnlock) throws VaultError {
        if (this.isLocked()) {
            if(this.doesVaultExist() || forceUnlock) {
                if (!this.authed) {
                    throw new MissingBiometricsError();
                } else {
                    super.unlock(forceUnlock);
                }
            }
        }
    }

    public void setAuthed() {
        this.authed = true;
    }

    public boolean isAuthed() {
        return this.authed;
    }
}
