package com.ionicframework.IdentityVault;

import android.app.Activity;
import android.os.Build;
import androidx.annotation.RequiresApi;
import androidx.appcompat.app.AppCompatActivity;

@RequiresApi(api = Build.VERSION_CODES.M)
public class SecureStorageVault extends VaultBase {

    public SecureStorageVault(IdentityVaultConfig config, AppCompatActivity activity) {
        super(config, activity);
    }

    @Override
    protected String getVaultType() {
        return VaultType.SECURE_STORAGE;
    }

    @Override
    public void lock() {
        return;
    }

    @Override
    public void unlock(boolean forceUnlock) throws VaultError {
        if (this.isLocked()) {
            if (this.doesVaultExist() || forceUnlock) {
                this.getData();
            }
        }
    }
}
